// App.tsx
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import {
  Main,
  Timeline,
  Expertise,
  Project,
  Contact,
  Navigation,
  Footer,
} from "./components";
import FadeIn from "./components/FadeIn";
import "./index.scss";

// Component con cho trang Home để code gọn hơn
const HomePage: React.FC = () => (
  <FadeIn transitionDuration={700}>
    <Main />
    <Expertise />
    <Timeline />
    <Project />
    <Contact />
  </FadeIn>
);

// Tạo 2 trang mới đơn giản (Bạn nên tách ra file riêng như PrivacyPolicy.tsx)
const PrivacyPolicy: React.FC = () => (
  <div style={{ paddingTop: "100px", minHeight: "80vh", textAlign: "center" }}>
    <h1>Privacy Policy</h1>
    <p>Nội dung chính sách bảo mật...</p>
  </div>
);

const TermOfUse: React.FC = () => (
  <div style={{ paddingTop: "100px", minHeight: "80vh", textAlign: "center" }}>
    <h1>Term of Use</h1>
    <p>Nội dung điều khoản sử dụng...</p>
  </div>
);

function App() {
  const [mode, setMode] = useState<string>("light");

  const handleModeChange = () => {
    setMode((prevMode) => (prevMode === "dark" ? "light" : "dark"));
  };

  useEffect(() => {
    window.scrollTo({ top: 0, left: 0, behavior: "smooth" });
  }, []);

  return (
    // Bọc Router ở ngoài cùng
    <Router>
      <div
        className={`main-container ${
          mode === "dark" ? "dark-mode" : "light-mode"
        }`}
      >
        <Navigation parentToChild={{ mode }} modeChange={handleModeChange} />

        {/* Khu vực nội dung thay đổi */}
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/privacy-policy" element={<PrivacyPolicy />} />
          <Route path="/term-of-use" element={<TermOfUse />} />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
