// src/data/projectsData.ts
import mock10 from "../assets/images/mock11.png";

// 1. Sửa lại Interface: Thay ReactNode bằng string (URL)
export interface ProjectType {
  id: string;
  title: string;
  image: string;
  description: string;
  link: string; // Link tải app/web
  privacyLink: string; // Đường dẫn đến trang Privacy
  termsLink: string; // Đường dẫn đến trang Terms
}

export const projectsData: ProjectType[] = [
  {
    id: "easy-write-ai",
    title: "Easy Write AI",
    image: mock10,
    description:
      "Developed a cross-platform English writing assistant for Vietnamese users with grammar correction and native language explanations using Flutter, Google Gemini API.",
    link: "https://play.google.com/store/apps/details?id=com.yourcompany.easywrite", // Link thật của bạn

    // 2. Điền đường dẫn thay vì component
    privacyLink: "/privacy-policy",
    termsLink: "/term-of-use",
  },
];
